catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || newValue == '') {
		return;
	}
	var itemSys = [];

	//Type appropriate comment here, and begin script below
	var payload = JSON.parse(g_form.getValue('authorities'));

	for(var i = 0; i<payload.length; i++){
		itemSys.push(payload[i].u_authority_type);
		var req_var = 'authorities_requestor_limit_'+(i+1);
		g_form.setValue(req_var, payload[i].u_requestor_amounts);
	}

	var ga = new GlideAjax('u_DALAjax');
	ga.addParam('sysparm_name','getAuthorityName');
	ga.addParam('sysparm_payload',itemSys);
	ga.getXML(callBack);

	function callBack(response){
		var answer = response.responseXML.documentElement.getAttribute('answer');
		var itemName = answer;
		itemName = itemName.split(',');
		for(var i=0; i<itemName.length;i++){
			g_form.addOption('authority_type',itemSys[i],itemName[i]);
			g_form.addOption('authority_type_two',itemSys[i],itemName[i]);
		}

	}



}
