catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    var Manager = g_form.getValue('u_manager');
    //alert('Manager :' + Manager);

    var ManagerLim = new GlideAjax('u_DALAjax');
    ManagerLim.addParam('sysparm_name', 'getManagerLim');
    ManagerLim.addParam('sysparm_requestor', Manager);
    ManagerLim.getXML(PopulateManagerLimit);

    function PopulateManagerLimit(response) {
        var answer = response.responseXML.documentElement.getAttribute("answer");
       // alert('populateManagerlimit : ' + answer);
        g_form.setValue('u_managers_authority_type_limit', answer);
    }
    //Type appropriate comment here, and begin script below

}
