catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || newValue == '') {
		return;
	}
	if (oldValue != newValue) {

		var Manager = g_form.getValue('u_manager');
		var authType = g_form.getValue('u_authority_type');
		//alert('Manager :' + Manager);
		//alert('Auth Type is: '+authType);

		var ManagerAuth = new GlideAjax('u_DALAjax');
		ManagerAuth.addParam('sysparm_name', 'getManagerAuth');
		ManagerAuth.addParam('sysparm_requestor', Manager);
		ManagerAuth.addParam('sysparm_auth', authType);
		ManagerAuth.getXML(PopulateManagerAuthLim);

	}

	function PopulateManagerAuthLim(response) {
		var answer = response.responseXML.documentElement.getAttribute("answer");
		answer = JSON.parse(answer);
		if(answer.limit_amount)
			g_form.setValue('u_managers_authority_type_limit', answer.limit_amount);
		if(answer.your_limit){
			g_form.setValue('your_limit', answer.your_limit);
			g_form.setReadOnly('your_limit', true);


		}
	}

	// 	var btn = this.document.getElementsByClassName("btn btn-default");
	// 	for (i = 0; i < btn.length; i++) {
	//         if(btn[i].innerText == 'Add') {
	//             btn[i].disabled="disabled";
	//             //If you want to hide it fully, then use below line.
	//             //btn[i].style.display='None';
	//         }
	//     }
}
