catalog_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below
	g_form.setDisplay('your_limit',false);
	g_form.setDisplay('authorities_requestor_limit_1',false);
	g_form.setDisplay('authorities_requestor_limit_2',false);
	g_form.setValue('u_requestor',g_user.userID);
	var requestor = g_form.getReference('u_requestor',userDetails);
}

function userDetails(requestor) {
	g_form.setValue('u_requestor_email',requestor.email);
	g_form.setValue('u_requestor_phone',requestor.phone);
	g_form.setValue('u_requestor_title',requestor.title);
	g_form.setValue('u_requestor_id', '003-456-732');
	g_form.setValue('u_requestor_manager',requestor.manager);
	g_form.setValue('u_manager_id', '024-564-123');
	g_form.setValue('u_requestor_limits',requestor.u_reference_3);
	g_form.setValue('u_glide_list_15',requestor.u_reference_1);
	g_form.setValue('u_business_unit',requestor.u_reference_2);
	g_form.setReadOnly('u_requestor_limits',true);
	g_form.setValue('u_requestor_department',requestor.department);

}
