sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

	// Add your code here
	var nowTime = new GlideDateTime();
	var parentTime = new GlideDateTime(current.parent_creation_date);
	var dur = GlideDateTime.subtract(nowTime, parentTime);
	var duration = dur.getNumericValue();
	var durationSeconds = (duration / 1000);
	gs.info('svk inside '+parentTime+' '+ (durationSeconds/60));
	if (durationSeconds / 60 < -1) {
		current.assigned_to = current.u_requestor.manager;
		current.state = -5;
		var grApprover = new GlideRecord('sysapproval_approver');
		grApprover.initialize();
		grApprover.sysapproval = current.getUniqueValue();
		grApprover.approver = current.u_requestor.manager;
		grApprover.state = 'requested';
		grApprover.insert();
		gs.eventQueue("x_snc_delegated_au.DAL.Approve.Notificat",current,current.u_requestor.manager);
	} else if(current.parent_creation_date){
//donothing
	}else {
		current.setAbortAction(true);
	}
})(current, previous);
