sys_script_fix.script:
var grDALChild = new GlideRecord("x_snc_delegated_au_child_dal");
grDALChild.addEncodedQuery("u_authority_typeISNOTEMPTY");
grDALChild.query();
while (grDALChild.next()) {
    grDALChild.u_authority_type = "";
	grDALChild.setWorkflow(false);
	grDALChild.autoSysFields(false);
    grDALChild.update();

}

gs.info("Number of records with Authority type has value" + grDALChild.getRowCount());
