sys_script_include.script:
var getDelRecords = Class.create();
getDelRecords.prototype = {
	initialize: function() {
	},

	getRecs : function(){
		var recs = [];
		var grDelegates = new GlideRecord('x_snc_delegated_au_delegated_authorities');
		grDelegates.addQuery('u_user', gs.getUserID());
		grDelegates.query();
		while(grDelegates.next()){
			recs.push(grDelegates.u_authority_type.toString());
		}
		return 'sys_idIN'+recs.toString();
	},
	getLimits : function(limit){
		gs.info('svk '+limit);
		var arr= [];
		var gr = new GlideRecord('x_snc_delegated_au_dal_amounts');
		gr.get(limit);
		var gLimit = gr.limit_amount;

		gs.info('vh '+gLimit);

		var grLim = new GlideRecord('x_snc_delegated_au_dal_amounts');
		grLim.addEncodedQuery("limit_amount<=javascript:global.getCurrencyFilter('x_snc_delegated_au_dal_amounts','limit_amount', 'USD;"+gLimit+"')");
		grLim.query();
		while(grLim.next()){
			arr.push(grLim.getUniqueValue());
		}
		return 'sys_idIN'+arr.toString();
	},



	type: 'getDelRecords'
};
