sysauto_script.script:
// Determine SI Metrics Start Time

var gr7 = new GlideRecord('metric_instance');
gr7.addQuery('sys_id', simFirstRec);
gr7.query();
if (gr7.next()) {
	simStart.setValue( gr7.start);
	simStart = simStart.toString();
} else {
	OK = false;
   gs.addErrorMessage('SECOPS: Reporting Demo Updates - Failed to find first SI Metric: ' + simFirstRec);
}

if (OK) {

	// Determine SI Metrics End Time

	gr8 = new GlideRecord('metric_instance');
	gr8.addQuery('sys_id', simLastRec);
	gr8.query();
	if (gr8.next ()) {
		simEnd.setValue( gr8.end);
		simEnd = simEnd.toString();
	} else {
		gs.addErrorMessage('SECOPS: Reporting Demo Updates - Failed to find last SI Metric: ' + simLastRec);
		OK = false;
	}
}

if (OK) {
	var rep1 = new GlideRecord('sys_report');
	if(rep1.get('1007bd90db328f000bee79daae96195d')) {

		var newFilt = rep1.filter;  
		newFilt = newFilt.replace(/2018\-08\-05/g, simEnd);   // Original report filter date
		newFilt = newFilt.replace(/2018\-07\-28/g, simStart); // Original report filter date
		rep1.filter = newFilt;

		gs.info('SECOPS: Reporting Demo Updates - Updating Report filters on: '+ rep1.title);
		rep1.update();
	}

	gs.info('simStart: '+ simStart);
	gs.info('simEnd: '+ simEnd);
}
