sysauto_script.script:
//Solutions
//fix software on vi

var vi = new GlideRecord('sn_vul_vulnerable_item');

//vi.addNullQuery('sw_vulnerability');

vi.query();

while (vi.next()) {
    var s = getSoftware(vi.vulnerability.software_vulnerabilities_raw);
    if (s) {
        vi.sw_vulnerability = s.sys_id + '';
        vi.update();
    }
}

function getSoftware(id) {
    if (id) {
        id = id.split(',')[0];
        id = id.slice(0, id.lastIndexOf(':'));
        id = id.slice(0, id.lastIndexOf(':'));

        var vs = new GlideRecord('sn_vul_software');
        vs.addQuery('raw_product', 'STARTSWITH', id);
        vs.query();
        if (vs.next()) {
            return vs;
        }
    }
}
