sysauto_script.script:
var narrativeDemo = new GlideRecord('x_snc_sec_dhu_app_narrative');
narrativeDemo.query();
//Look at existing Narrative Demo records
//Get their title and referenced narrative sys id
while (narrativeDemo.next()){
	var narrativeID = narrativeDemo.getValue('narrative');
	var narrativeTitle = narrativeDemo.getValue('title');
	//Use the title to find the correct narrative record
	var narrative = new GlideRecord('x_snc_demohub_app_narrative');
	narrative.addQuery('title',narrativeTitle);
	narrative.query();
	if(narrative.next()){
		var newNarrativeID = narrative.sys_id;
		//Replace the narrative reference value on this Narrative Demo record
		narrativeDemo.setValue('narrative',newNarrativeID);
		narrativeDemo.update();
	}
}
