sysauto_script.script:
// Delete undesired columns from Netstat Enrichment Related List

var listElement = new GlideRecord('sys_ui_list_element');
listElement.addQuery('list_id', '26de0d71b3d01300743cc9c316a8dc45');
var elementCondition2 = listElement.addQuery('element', 'path');
elementCondition2.addOrCondition('element', 'hash_value');
elementCondition2.addOrCondition('element', 'source');
elementCondition2.addOrCondition('element', 'sys_created_on');
listElement.query();

while(listElement.next()) {
  if (listElement.list_id.view == '44a655f4b3901300743cc9c316a8dc61') {
    gs.info('SECOPS Demo: Deleting list element ' + listElement.element + ', Position: ' + listElement.position);
    listElement.deleteRecord();
  } 
}
