sysauto_script.script:
var vit = new GlideRecord('sn_vul_vulnerable_item');
vit.addEncodedQuery('vulnerability.idENDSWITHCVE-2019-0785');
vit.query();

var riskScore = new GlideRecord('x_refui_vr_risk_score');
riskScore.addEncodedQuery('vulnerability_entry.idSTARTSWITHCVE-2019-0785');
riskScore.query();
riskScore.next();
var rfVulnID = riskScore.getValue('vulnerability_entry');


while(vit.next()){
gs.debug('rfVulnID: '+rfVulnID);
gs.debug('vitVulnID: '+ vit.getValue('vulnerability'));
vit.setValue('vulnerability',rfVulnID);
vit.update();

var securityCalc = new sn_vul.VulnerabilityCalculator(vit, true, true, "sn_vul_calculator_group", "sn_vul_calculator");
securityCalc.calculate();

}

var rfConnector = new GlideRecord('x_refui_core_integration_connector');
rfConnector.query();

while(rfConnector.next()){
	gs.debug('Deleting API Token value from RecordedFuture Integration Connector: '+ rfConnector.getValue('name'));
	rfConnector.setValue('api_token','');
	rfConnector.setValue('vr_api_token','');
	rfConnector.update();
}
