sysauto_script.script:
//fix preferred solution on cves

var cve = new GlideRecord('sn_vul_entry');
cve.addEncodedQuery('preferred_solution.sys_idISEMPTY^preferred_solutionISNOTEMPTY');
cve.query();
cve.setValue('preferred_solution','NULL');
cve.setWorkflow(false);
cve.updateMultiple();

var  grVuln = new GlideRecord("sn_vul_entry");
grVuln.addNotNullQuery("preferred_solution");
grVuln.query();
while(grVuln.next()) {
  var grVulItem = new GlideRecord('sn_vul_vulnerable_item');
  grVulItem.addQuery('vulnerability', grVuln.getUniqueValue());
  grVulItem.query();
  if(grVulItem.hasNext()) {
      grVulItem.setValue('preferred_solution', grVuln.preferred_solution);
      grVulItem.updateMultiple();
  }
}
