sysauto_script.script:
// Delete Vuln Item PA Scores older than oldest vuln item -----------------------
// This fixes a data issue with the original secops/grc demo pack

var vulnItem = new GlideRecord('sn_vul_vulnerable_item');
vulnItem.addQuery('first_found', '!=', ''); 
vulnItem.addQuery('sys_id', '!=', '4bfa7c0adbffc7009ab4f9b61d961979');
vulnItem.orderBy('first_found');
vulnItem.query();

if (vulnItem.next()) {
  var firstDate = vulnItem.first_found;
  firstDate = firstDate.replace(/-/g,'');
  gs.info(firstDate);
  var sdu = new global.SecurityDemoUtils();
  sdu.purgePAScores('Vulnerable Items', '20150101', firstDate);
}
